  onselectPais(objSelected){
    console.log(objSelected)
    if (objSelected != undefined) {
      this.formularioService.getPaisLocalidad(objSelected)
        .subscribe(data => {
          let res: any = data
          if (res.length > 0) {
            this.paisLocalidad = res
          } else {
            this.paisLocalidad = [];
            alert("LOCALIDAD:"+res.msg)
            console.log("pais localidad",data)
          }
        },
          err => console.log(err)
        );
    }
  }




  onselectDepartamento(objSelected) {
    //this.artista.id_mun = null;
    //this.artista.id_prov = null;
    console.log(objSelected)
    if (objSelected != undefined) {
      this.formularioService.getProvincias(objSelected)
        .subscribe(data => {
          let res: any = data
          if (res.length > 0) {
            this.provincias = res
            this.artista.id_prov = objSelected.IdProv;
            this.artista.dptoProv = objSelected.DepProv;
            this.artista.prov = objSelected.Prov;
          } else {
            this.provincias = [];
            alert("DEPARTAMENTO:"+res.msg)
            console.log("DEPARTAMENTO",data)
          }
        },
          err => console.log(err)
        );
    }
  }

